var NotifyChannelStatus = function(){
    var self = this;
    self.info = [
        {id:-1, code:'unused', des:"停止"},
        {id:0, code:'waiting', des:"等待中"},
        {id:1, code:'running', des:"运行中"}
    ];
    self.infoArray = {};
    self.init();
};

NotifyChannelStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

NotifyChannelStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new NotifyChannelStatus();