var NotifyMsgStatus = function(){
    var self = this;
    self.info = [
        {id:1, code:'unused', des:"未处理"},
        {id:2, code:'used', des:"已经处理"}
    ];
    self.infoArray = {};
    self.init();
};

NotifyMsgStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

NotifyMsgStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new NotifyMsgStatus();