var OrderPayType = function(){
    var self = this;
    self.info = [
        {id:0, code:'company', des:'第三方支付'},
        {id:1, code:'cash', des:'现金支付'}
    ];
    self.infoArray = {};
    self.init();
};

OrderPayType.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

OrderPayType.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new OrderPayType();