var OrderStatus = function(){
    var self = this;
    self.info = [{id:1000, code:'init', des:"初始状态"},
        {id:1001, code:'presale', des:"预售"},
        {id:1100, code:'waiting_print', des:"等待出票"},
        {id:1200, code:'success', des:"出票成功"},
        {id:1300, code:'partial_success', des:"部分出票成功"},
        {id:1400, code:'FAILURE', des:"交易失败"},
        {id:1500, code:'CANCEL', des:"交易取消"},
        {id:1600, code:'WAITING_PAY', des:"等待支付"},
        {id:1700, code:'WAITING_CONFIRM', des:"等待确认"},
        {id:1800, code:'NOT_HIT', des:"未中奖"},
        {id:1810, code:'HIT', des:"已中奖"},
        {id:1900, code:'PRIZED', des:"已返奖"},
        {id:2100, code:'WAITING_BIG_PRIZING', des:"等待返大奖"}
    ];
    self.infoArray = {};
    self.init();
};

OrderStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

OrderStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new OrderStatus();