var PrintStatus = function(){
    var self = this;
    self.info = [
        {id:0, code:'success', des:"出票成功"},
        {id:1, code:'failure', des:"出票失败"}
    ];
    self.infoArray = {};
    self.init();
};

PrintStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};
PrintStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};
module.exports = new PrintStatus();