var ReceiptStatus = function(){
    var self = this;
    self.info = [{id:1110, code:'NOT_TAKE_AWAY', des:"未领取"},
        {id:1210, code:'TAKE_AWAY', des:"已领取"},
        {id:1310, code:'NOT_CLAIM_PRIZE', des:"未兑奖"},
        {id:1410, code:'NOT_HIT', des:"未中奖"},
        {id:1510, code:'SYSTEM_TAKEWAY', des:"程序取走"},
        {id:1610, code:'CLAIM_SUCCESS', des:"兑奖成功"},
        {id:1710, code:'CLAIM_FAILURE', des:"兑奖失败"}
    ];
    self.infoArray = {};
    self.init();
};
ReceiptStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};
ReceiptStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};
module.exports = new ReceiptStatus();