var SchemeStatus = function(){
    var self = this;
    self.info = [{id:1010, code:'NOT_AVAILABLE', des:"未生效"},
        {id:1110, code:'RUNNING', des:"进行中"},
        {id:1200, code:'CANCELING', des:"取消中"},
        {id:1210, code:'CANCELED', des:"已取消"},
        {id:1310, code:'REFUNDED', des:"已退款"},
        {id:1410, code:'FINISHED', des:"已完成"},
        {id:1405, code:'WAIT_AUDIT', des:"等待结算"},
        {id:1500, code:'AUDITING', des:"结算中"},
        {id:1510, code:'AUDITED', des:"已结算"},
        {id:1610, code:'OVER', des:"已完结"}
    ];
    self.infoArray = {};
    self.init();
};
SchemeStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};
SchemeStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};
module.exports = new SchemeStatus();