var SchemeType = function(){
    var self = this;
    self.info = [
        {id:1, code:'none', des:'无'},
        {id:2, code:'follow', des:'追号'}
    ];
    self.infoArray = {};
    self.init();
};

SchemeType.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

SchemeType.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new SchemeType();