var ssq = require('mcp_ssq').ssq;

/**
 * 算奖的工场类
 * @constructor
 */
var CheckFactory = function()
{
}

CheckFactory.prototype.getByGameCode = function(gameCode)
{
    if(gameCode == 'F01')
    {
        return ssq.check();
    }
    else
    {
        return null;
    }
}

module.exports = new CheckFactory();