var http = require('http');
var querystring = require('querystring');

var config = require('mcp_config');
var prop = config.prop;
var gts = prop.platform.gateways;
var pgts = prop.printGts;

var esut = require('easy_util');
var digestUtil = esut.digestUtil;
var dateUtil = esut.dateUtil;
var log = esut.log;

var PlatInterUtil = function(){};

PlatInterUtil.prototype.get = function(userId, userType, channelCode, digestType, userKey, cmd, body, cb)
{
    body.uniqueId = digestUtil.createUUID();
    var bodyStr = JSON.stringify(body);
    var head = {userId:userId, userType:userType, channelCode:channelCode, digest:"", digestType:digestType, cmd:cmd, ver:prop.platform.ver,
    id:digestUtil.createUUID()};
    head.timestamp = dateUtil.getOldCurTime();
    var encodedBody = bodyStr;
    if(head.digestType.length > 0)
    {
        encodedBody = digestUtil.generate(head, userKey, bodyStr);
    }
    var msgJson = {head:head, body:encodedBody};
    log.info(msgJson);
    var msgToSend = JSON.stringify(msgJson);
    var post_data = querystring.stringify({
        message:msgToSend
    });
    var headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Content-Length':post_data.length
    };
    var ran = Math.round(Math.random()*10);
    var gtIndex = ran%gts.length;
    var gt = gts[gtIndex];
    var options = {host:gt.host, port:gt.port, method:gt.method, path:"/mcp-filter/main/interface.htm"};
    options.headers = headers;
    var req = http.request(options, function(res) {
        res.setEncoding('utf8');
        var data = '';
        res.on('data', function (chunk) {
            data += chunk;
        });
        res.on('end', function(){
            cb(null, JSON.parse(data));
        });
    });
    req.setTimeout(20000);
    req.on('error', function(e) {
        cb(e, null);
    });
    req.write(post_data, "utf8");
    req.end();
};

PlatInterUtil.prototype.getNew = function(msg, cb)
{
    var self = this;
    var post_data = querystring.stringify({
        message:msg
    });
    var headers = {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Content-Length':post_data.length
    };

    var path = "/mcp-filter/main/interface.htm";

    var ran = Math.round(Math.random()*100);
    var gtIndex = ran%pgts.length;
    var gt = pgts[gtIndex];
    var options = {host:gt.host, port:gt.port, method:gt.method, path:path};
    options.headers = headers;

    self.send(post_data, options, function(err, data){
        if(err)     //如果发生错误，切换一个，再次发送
        {
            log.info(err);
            gtIndex = (gtIndex + 1)%pgts.length;
            gt = pgts[gtIndex];
            options = {host:gt.host, port:gt.port, method:gt.method, path:path};
            options.headers = headers;

            self.send(post_data, options, function(err, data){
                cb(err, data);
            });
        }
        else
        {
            cb(err, data);
        }
    });
};


PlatInterUtil.prototype.send = function(data, options, cb)
{
    var self = this;
    var backCalled = false; //是否已经调用过回调函数
    var req = http.request(options, function(res) {
        res.setEncoding('utf8');
        var backData = '';
        res.on('data', function (chunk) {
            backData += chunk;
        });
        res.on('end', function(){
            if(!backCalled)
            {
                cb(null, backData);
                backCalled = true;
            }
        });
    });
    req.setTimeout(20000);
    req.on('error', function(e) {
        if(!backCalled)
        {
            cb(e, null);
            backCalled = true;
        }
    });
    req.write(data, "utf8");
    req.end();
};

var platInterUtil = new PlatInterUtil();
module.exports = platInterUtil;